import React, { useEffect, useState } from "react";
import { getBooks } from "../api";
import { useNavigate } from "react-router-dom";
import axios from "axios";

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø –¥–ª—è –∫–Ω–∏–≥–∏
interface Book {
  id: number;
  title: string;
  genre: string;
  authorName: string;
  isAvailable: boolean;
}

const BooksList: React.FC = () => {
  // –£–∫–∞–∑—ã–≤–∞–µ–º —Ç–∏–ø –¥–ª—è —Å–æ—Å—Ç–æ—è–Ω–∏—è books
  const [books, setBooks] = useState<Book[]>([]);
  const [search, setSearch] = useState("");
  const [genre, setGenre] = useState("");
  const [author, setAuthor] = useState("");
  const [page, setPage] = useState(1);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
    checkAdmin();
  }, [search, genre, author, page]);

  const fetchBooks = async () => {
    try {
      const data = await getBooks(search, genre, author, page);
      console.log("–î–∞–Ω–Ω—ã–µ —Å —Å–µ—Ä–≤–µ—Ä–∞:", data); // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö

      const booksWithAvailability = await Promise.all(
        data.map(async (book: any) => {
          try {
            // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –∑–∞–ø–∏—Å—å –æ –∫–Ω–∏–≥–µ –≤ —Ç–∞–±–ª–∏—Ü–µ RentalBooks
            const response = await axios.get(`http://localhost:7143/api/bookrentals/${book.id}`);
            console.log("–û—Ç–≤–µ—Ç –æ—Ç bookrentals:", response.data); // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –æ—Ç–≤–µ—Ç–∞

            // –ï—Å–ª–∏ –µ—Å—Ç—å —Ö–æ—Ç—è –±—ã –æ–¥–Ω–∞ –∑–∞–ø–∏—Å—å, –∫–Ω–∏–≥–∞ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞
            const isRented = response.data.length > 0;
            return { ...book, isAvailable: !isRented };
          } catch (error) {
            // –ï—Å–ª–∏ –∫–Ω–∏–≥–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞ –≤ —Ç–∞–±–ª–∏—Ü–µ RentalBooks, —Å—á–∏—Ç–∞–µ–º –µ—ë –¥–æ—Å—Ç—É–ø–Ω–æ–π
            if (axios.isAxiosError(error) && error.response?.status === 404) {
              return { ...book, isAvailable: true };
            }
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç–∏ –∫–Ω–∏–≥–∏:", error);
            return { ...book, isAvailable: false }; // –í —Å–ª—É—á–∞–µ –¥—Ä—É–≥–∏—Ö –æ—à–∏–±–æ–∫ —Å—á–∏—Ç–∞–µ–º –∫–Ω–∏–≥—É –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–æ–π
          }
        })
      );

      setBooks(booksWithAvailability);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–Ω–∏–≥:", error);
    }
  };

  const checkAdmin = () => {
    const token = localStorage.getItem("token");

    if (!token) {
      console.warn("üî¥ –¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç.");
      setIsAdmin(false);
      return;
    }

    console.log("üìå –ü–æ–ª—É—á–µ–Ω–Ω—ã–π —Ç–æ–∫–µ–Ω:", token);

    try {
      const parts = token.split(".");
      if (parts.length !== 3) {
        throw new Error("‚ö†Ô∏è –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç —Ç–æ–∫–µ–Ω–∞ (JWT –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å 3 —á–∞—Å—Ç–∏)");
      }

      const base64Url = parts[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const decodedPayload = JSON.parse(atob(base64));

      console.log("üìå –î–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —Ç–æ–∫–µ–Ω–∞:", decodedPayload);

      const userRole =
        decodedPayload["http://schemas.microsoft.com/ws/2008/06/identity/claims/role"];

      if (userRole) {
        setIsAdmin(userRole.toLowerCase() === "admin");
      } else {
        console.warn("‚ö†Ô∏è –†–æ–ª—å –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç –≤ —Ç–æ–∫–µ–Ω–µ.");
        setIsAdmin(false);
      }
    } catch (error) {
      console.error("üõë –û—à–∏–±–∫–∞ –¥–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", error);
      setIsAdmin(false);
    }
  };

  useEffect(() => {
    console.log("–¢–µ–∫—É—â–∏–µ –∫–Ω–∏–≥–∏:", books); // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è books
  }, [books]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">–°–ø–∏—Å–æ–∫ –∫–Ω–∏–≥</h1>

      {/* –ö–Ω–æ–ø–∫–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –∫–Ω–∏–≥–∏ (—Ç–æ–ª—å–∫–æ –¥–ª—è –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞) */}
      {isAdmin && (
        <button
          className="bg-green-500 text-white px-4 py-2 mb-4"
          onClick={() => navigate("/add-book")}
        >
          ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∫–Ω–∏–≥—É
        </button>
      )}

      {/* –ü–æ–ª—è —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="–ü–æ–∏—Å–∫ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é..."
          className="border p-2"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="text"
          placeholder="–í–≤–µ–¥–∏—Ç–µ –∂–∞–Ω—Ä..."
          className="border p-2"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
        <input
          type="text"
          placeholder="–í–≤–µ–¥–∏—Ç–µ –∏–º—è –∞–≤—Ç–æ—Ä–∞..."
          className="border p-2"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
      </div>

      {/* –°–ø–∏—Å–æ–∫ –∫–Ω–∏–≥ */}
      <div className="grid grid-cols-3 gap-4">
        {books.length === 0 ? (
          <p>–ö–Ω–∏–≥–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</p>
        ) : (
          books.map((book) => (
            <div
              key={book.id}
              className="border p-4 rounded shadow cursor-pointer"
              onClick={() => navigate(`/books/${book.id}`)}
            >
              <h2 className="text-xl font-bold">{book.title}</h2>
              <p>{book.genre}</p>
              <p>–ê–≤—Ç–æ—Ä: {book.authorName}</p>
              <p className={book.isAvailable ? "text-green-500" : "text-red-500"}>
                {book.isAvailable ? "–í –Ω–∞–ª–∏—á–∏–∏" : "–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏"}
              </p>
            </div>
          ))
        )}
      </div>

      {/* –ü–∞–≥–∏–Ω–∞—Ü–∏—è */}
      <div className="flex justify-center mt-4">
        <button className="p-2 border" onClick={() => setPage(page - 1)} disabled={page === 1}>
          ‚¨ÖÔ∏è –ù–∞–∑–∞–¥
        </button>
        <span className="p-2">{page}</span>
        <button className="p-2 border" onClick={() => setPage(page + 1)}>
          –í–ø–µ—Ä–µ–¥ ‚û°Ô∏è
        </button>
      </div>
    </div>
  );
};

export default BooksList;
