import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddBook: React.FC = () => {
  const [isbn, setIsbn] = useState("");
  const [title, setTitle] = useState("");
  const [genre, setGenre] = useState("");
  const [description, setDescription] = useState("");
  const [authorId, setAuthorId] = useState<string | null>(null);
  const [authors, setAuthors] = useState<{ id: number; firstName: string; lastName: string }[]>([]);
  const [showAddAuthor, setShowAddAuthor] = useState(false);
  const [newAuthor, setNewAuthor] = useState({
    firstName: "",
    lastName: "",
    birthDate: "",
    country: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get("http://localhost:7143/api/authors");
      setAuthors(response.data);
    } catch (error) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ Ð°Ð²Ñ‚Ð¾Ñ€Ð¾Ð²:", error);
    }
  };

  const handleAddBook = async () => {
    try {
      let finalAuthorId = authorId;

      if (!authorId) {
        const authorResponse = await axios.post("http://localhost:7143/api/authors", newAuthor);
        finalAuthorId = authorResponse.data.id;
      }

      await axios.post("http://localhost:7143/api/books", {
        isbn,
        title,
        genre,
        description,
        authorId: finalAuthorId,
        takenAt: new Date().toISOString(),
        returnAt: new Date().toISOString(),
      });

      navigate("/books");
    } catch (error) {
      console.error("ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð´Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ð¸ ÐºÐ½Ð¸Ð³Ð¸:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ ÐºÐ½Ð¸Ð³Ð¸</h1>

      <input
        className="border p-2 mb-2 w-full"
        type="text"
        placeholder="ISBN"
        value={isbn}
        onChange={(e) => setIsbn(e.target.value)}
      />
      <input
        className="border p-2 mb-2 w-full"
        type="text"
        placeholder="ÐÐ°Ð·Ð²Ð°Ð½Ð¸Ðµ ÐºÐ½Ð¸Ð³Ð¸"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        className="border p-2 mb-2 w-full"
        type="text"
        placeholder="Ð–Ð°Ð½Ñ€"
        value={genre}
        onChange={(e) => setGenre(e.target.value)}
      />
      <textarea
        className="border p-2 mb-2 w-full"
        placeholder="ÐžÐ¿Ð¸ÑÐ°Ð½Ð¸Ðµ"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />

      <select
        className="border p-2 mb-2 w-full"
        value={authorId || ""}
        onChange={(e) => setAuthorId(e.target.value)}
      >
        <option value="">Ð’Ñ‹Ð±ÐµÑ€Ð¸Ñ‚Ðµ Ð°Ð²Ñ‚Ð¾Ñ€Ð°</option>
        {authors.map((author) => (
          <option key={author.id} value={author.id}>
            {author.firstName} {author.lastName}
          </option>
        ))}
      </select>

      <button
        className="bg-gray-500 text-white px-4 py-2 mb-2"
        onClick={() => setShowAddAuthor(true)}
      >
        âž• Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð¾Ð²Ð¾Ð³Ð¾ Ð°Ð²Ñ‚Ð¾Ñ€Ð°
      </button>

      {showAddAuthor && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Ð”Ð¾Ð±Ð°Ð²Ð»ÐµÐ½Ð¸Ðµ Ð°Ð²Ñ‚Ð¾Ñ€Ð°</h2>
            <input
              className="border p-2 mb-2 w-full"
              type="text"
              placeholder="Ð˜Ð¼Ñ"
              value={newAuthor.firstName}
              onChange={(e) => setNewAuthor({ ...newAuthor, firstName: e.target.value })}
            />
            <input
              className="border p-2 mb-2 w-full"
              type="text"
              placeholder="Ð¤Ð°Ð¼Ð¸Ð»Ð¸Ñ"
              value={newAuthor.lastName}
              onChange={(e) => setNewAuthor({ ...newAuthor, lastName: e.target.value })}
            />
            <input
              className="border p-2 mb-2 w-full"
              type="date"
              placeholder="Ð”Ð°Ñ‚Ð° Ñ€Ð¾Ð¶Ð´ÐµÐ½Ð¸Ñ"
              value={newAuthor.birthDate}
              onChange={(e) => setNewAuthor({ ...newAuthor, birthDate: e.target.value })}
            />
            <input
              className="border p-2 mb-2 w-full"
              type="text"
              placeholder="Ð¡Ñ‚Ñ€Ð°Ð½Ð°"
              value={newAuthor.country}
              onChange={(e) => setNewAuthor({ ...newAuthor, country: e.target.value })}
            />
            <div className="flex justify-between">
              <button className="bg-blue-500 text-white px-4 py-2" onClick={() => setShowAddAuthor(false)}>
                Ð—Ð°ÐºÑ€Ñ‹Ñ‚ÑŒ
              </button>
            </div>
          </div>
        </div>
      )}

      <button className="bg-green-500 text-white px-4 py-2 mt-4" onClick={handleAddBook}>
        ðŸ“š Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ ÐºÐ½Ð¸Ð³Ñƒ
      </button>
    </div>
  );
};

export default AddBook;
