import React, { useEffect, useState } from "react";
import { getBooks } from "../api";

const BooksList: React.FC = () => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState("");
  const [genre, setGenre] = useState("");
  const [page, setPage] = useState(1);

  useEffect(() => {
    fetchBooks();
  }, [search, genre, page]);

  const fetchBooks = async () => {
    try {
      const data = await getBooks(search, genre, page);
      setBooks(data);
    } catch (error) {
      console.error("Ошибка при загрузке книг:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Список книг</h1>

      {/* Поля фильтрации */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Поиск по названию..."
          className="border p-2"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="text"
          placeholder="Введите жанр..."
          className="border p-2"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
      </div>

      {/* Список книг */}
      <div className="grid grid-cols-3 gap-4">
        {books.length === 0 ? (
          <p>Книги не найдены</p>
        ) : (
          books.map((book: any) => (
            <div key={book.id} className="border p-4 rounded shadow">
              <h2 className="text-xl font-bold">{book.title}</h2>
              <p>{book.genre}</p>
            </div>
          ))
        )}
      </div>

      {/* Пагинация */}
      <div className="flex justify-center mt-4">
        <button className="p-2 border" onClick={() => setPage(page - 1)} disabled={page === 1}>
          ⬅️ Назад
        </button>
        <span className="p-2">{page}</span>
        <button className="p-2 border" onClick={() => setPage(page + 1)}>
          Вперед ➡️
        </button>
      </div>
    </div>
  );
};

export default BooksList;
