import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const EditBook: React.FC = () => {
  const { id } = useParams<{ id: string }>(); // –ü–æ–ª—É—á–∞–µ–º id –∫–Ω–∏–≥–∏ –∏–∑ URL
  const navigate = useNavigate();

  const [isbn, setIsbn] = useState("");
  const [title, setTitle] = useState("");
  const [genre, setGenre] = useState("");
  const [description, setDescription] = useState("");
  const [authorId, setAuthorId] = useState<string | null>(null);
  const [authors, setAuthors] = useState<{ id: number; firstName: string; lastName: string }[]>([]);
  const [showAddAuthor, setShowAddAuthor] = useState(false);
  const [newAuthor, setNewAuthor] = useState({
    firstName: "",
    lastName: "",
    birthDate: "",
    country: "",
  });

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫–Ω–∏–≥–∏ –∏ –∞–≤—Ç–æ—Ä–æ–≤ –ø—Ä–∏ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
  useEffect(() => {
    fetchBook();
    fetchAuthors();
  }, [id]);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∫–Ω–∏–≥–∏ –ø–æ –µ—ë id
  const fetchBook = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}`);
      const book = response.data;

      // –ó–∞–ø–æ–ª–Ω—è–µ–º –ø–æ–ª—è —Ñ–æ—Ä–º—ã –¥–∞–Ω–Ω—ã–º–∏ –∏–∑ –∫–Ω–∏–≥–∏
      setIsbn(book.isbn);
      setTitle(book.title);
      setGenre(book.genre);
      setDescription(book.description);
      setAuthorId(book.authorId.toString());
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–Ω–∏–≥–∏:", error);
    }
  };

  // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–ø–∏—Å–æ–∫ –∞–≤—Ç–æ—Ä–æ–≤
  const fetchAuthors = async () => {
    try {
      const response = await axios.get("http://localhost:7143/api/authors");
      setAuthors(response.data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∞–≤—Ç–æ—Ä–æ–≤:", error);
    }
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –Ω–æ–≤–æ–≥–æ –∞–≤—Ç–æ—Ä–∞
  const handleAddAuthor = async () => {
    try {
      const response = await axios.post("http://localhost:7143/api/authors", newAuthor);
      const addedAuthor = response.data;

