import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const BookPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [book, setBook] = useState<any>(null);
  const [isAvailable, setIsAvailable] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [returnDate, setReturnDate] = useState("");

  useEffect(() => {
    fetchBook();
    checkAvailability();
  }, [id]);

  const fetchBook = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}`);
      setBook(response.data);
    } catch (error) {
      console.error("Ошибка при загрузке книги:", error);
    }
  };

  const checkAvailability = async () => {
    try {
      // Проверяем, есть ли записи о книге в таблице BookRentals
      const response = await axios.get(`http://localhost:7143/api/books/${id}/rentals`);
      console.log("Ответ от bookrentals:", response.data); // Логирование ответа

      // Если есть хотя бы одна запись, книга недоступна
      const isRented = response.data.length > 0;
      setIsAvailable(!isRented);
    } catch (error) {
      console.error("Ошибка при проверке наличия книги:", error);
      setIsAvailable(false); // В случае ошибки считаем книгу недоступной
    }
  };

  const handleBorrowBook = async () => {
    try {
      const response = await axios.post("http://localhost:7143/api/books/borrow", {
        bookId: id,
        returnAt: returnDate,
      });

      console.log("Книга успешно взята:", response.data);
      setShowModal(false);
      setIsAvailable(false); // После взятия книги она становится недоступной
    } catch (error) {
      console.error("Ошибка при взятии книги:", error);
    }
  };

  if (!book) return <p>Загрузка...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{book.title}</h1>
      <p><strong>ISBN:</strong> {book.isbn}</p>
      <p><strong>Жанр:</strong> {book.genre}</p>
      <p><strong>Описание:</strong> {book.description}</p>
      <p><strong>Автор:</strong> {book.authorName}</p>
      <p>
        <strong>Статус:</strong>{" "}
        <span className={isAvailable ? "text-green-500" : "text-red-500"}>
          {isAvailable ? "В наличии" : "Нет в наличии"}
        </span>
      </p>

      {isAvailable ? (
        <button className="bg-blue-500 text-white px-4 py-2 mt-4" onClick={() => setShowModal(true)}>
          📖 Взять книгу
        </button>
      ) : (
        <p className="text-red-500 font-bold">❌ Нет в наличии</p>
      )}

      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Выберите дату возврата</h2>
            <input
              className="border p-2 mb-2 w-full"
              type="date"
              min={new Date().toISOString().split("T")[0]}
              max={new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split("T")[0]}
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
            />
            <div className="flex justify-between">
              <button className="bg-green-500 text-white px-4 py-2" onClick={handleBorrowBook}>
                ✅ Взять книгу
              </button>
              <button className="bg-gray-500 text-white px-4 py-2" onClick={() => setShowModal(false)}>
                ❌ Отмена
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookPage;
