import React, { useEffect, useState } from "react";
import { getBooks } from "../api";
import { useNavigate } from "react-router-dom";
import axios from "axios";

interface Book {
  id: number;
  title: string;
  genre: string;
  authorName: string;
  isAvailable: boolean;
}

const BooksList: React.FC = () => {
  const [books, setBooks] = useState<Book[]>([]);
  const [notifications, setNotifications] = useState<string[]>([]);
  const [search, setSearch] = useState("");
  const [genre, setGenre] = useState("");
  const [author, setAuthor] = useState("");
  const [page, setPage] = useState(1);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
    fetchNotifications();
    checkAdmin();
  }, [search, genre, author, page]);

  // üìå –ó–∞–ø—Ä–æ—Å —Å–ø–∏—Å–∫–∞ –∫–Ω–∏–≥
  const fetchBooks = async () => {
    try {
      const data = await getBooks(search, genre, author, page);
      
      const booksWithAvailability = await Promise.all(
        data.map(async (book: any) => {
          try {
            // –ü–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ–± –∞—Ä–µ–Ω–¥–µ –∫–Ω–∏–≥–∏
            const response = await axios.get(`http://localhost:7143/api/books/${book.id}/is-rented`, {
              headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
            });

            // –ï—Å–ª–∏ –∫–Ω–∏–≥–∞ –∞—Ä–µ–Ω–¥—É–µ—Ç—Å—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–º, –ø–æ–º–µ—á–∞–µ–º –µ—ë –∫–∞–∫ –Ω–µ–¥–æ—Å—Ç—É–ø–Ω—É—é
            return { ...book, isAvailable: !response.data.isRented };
          } catch (error) {
            console.error(`–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç–∏ –∫–Ω–∏–≥–∏ (ID ${book.id}):`, error);
            return { ...book, isAvailable: true }; // –ü–æ —É–º–æ–ª—á–∞–Ω–∏—é —Å—á–∏—Ç–∞–µ–º –∫–Ω–∏–≥—É –¥–æ—Å—Ç—É–ø–Ω–æ–π
          }
        })
      );

      setBooks(booksWithAvailability);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–Ω–∏–≥:", error);
    }
  };

  // üìå –ó–∞–ø—Ä–æ—Å —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π –æ –≤–æ–∑–≤—Ä–∞—Ç–µ –∫–Ω–∏–≥
  const fetchNotifications = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const response = await axios.get("http://localhost:7143/api/books/user/rentals/notifications", {
        headers: { Authorization: `Bearer ${token}` }
      });

      setNotifications(response.data.map((n: any) => n.message));
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–π:", error);
    }
  };

  // üìå –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ä–æ–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const checkAdmin = () => {
    const token = localStorage.getItem("token");

    if (!token) {
      setIsAdmin(false);
      return;
    }

    try {
      const parts = token.split(".");
      const base64Url = parts[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const decodedPayload = JSON.parse(atob(base64));

      const userRole = decodedPayload["http://schemas.microsoft.com/ws/2008/06/identity/claims/role"];
      setIsAdmin(userRole?.toLowerCase() === "admin");
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –¥–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–æ–∫–µ–Ω–∞:", error);
      setIsAdmin(false);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">üìö –°–ø–∏—Å–æ–∫ –∫–Ω–∏–≥</h1>

      {/* üîî –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è */}
      {notifications.length > 0 && (
        <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-4">
          {notifications.map((message, index) => (
            <p key={index}>‚ö†Ô∏è {message}</p>
          ))}
        </div>
      )}

      {/* –ö–Ω–æ–ø–∫–∏ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏ */}
      <div className="flex gap-2 mb-4">
        <button className="bg-blue-500 text-white px-4 py-2" onClick={() => navigate("/profile")}>
          üë§ –õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç
        </button>

        {isAdmin && (
          <button className="bg-green-500 text-white px-4 py-2" onClick={() => navigate("/add-book")}>
            ‚ûï –î–æ–±–∞–≤–∏—Ç—å –∫–Ω–∏–≥—É
          </button>
        )}
      </div>

      {/* –ü–æ–ª—è –ø–æ–∏—Å–∫–∞ –∏ —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="–ü–æ–∏—Å–∫ –ø–æ –Ω–∞–∑–≤–∞–Ω–∏—é..."
          className="border p-2"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="text"
          placeholder="–§–∏–ª—å—Ç—Ä –ø–æ –∂–∞–Ω—Ä—É..."
          className="border p-2"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
        <input
          type="text"
          placeholder="–§–∏–ª—å—Ç—Ä –ø–æ –∞–≤—Ç–æ—Ä—É..."
          className="border p-2"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
      </div>

      {/* –°–ø–∏—Å–æ–∫ –∫–Ω–∏–≥ */}
      <div className="grid grid-cols-3 gap-4">
        {books.length === 0 ? (
          <p>–ö–Ω–∏–≥–∏ –Ω–µ –Ω–∞–π–¥–µ–Ω—ã</p>
        ) : (
          books.map((book) => (
            <div key={book.id} className="border p-4 rounded shadow cursor-pointer" onClick={() => navigate(`/books/${book.id}`)}>
              <h2 className="text-xl font-bold">{book.title}</h2>
              <p>{book.genre}</p>
              <p>–ê–≤—Ç–æ—Ä: {book.authorName}</p>
              <p className={book.isAvailable ? "text-green-500" : "text-red-500"}>
                {book.isAvailable ? "–í –Ω–∞–ª–∏—á–∏–∏" : "–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏"}
              </p>
            </div>
          ))
        )}
      </div>

      {/* –ü–∞–≥–∏–Ω–∞—Ü–∏—è */}
      <div className="flex justify-center mt-4">
        <button className="p-2 border" onClick={() => setPage(page - 1)} disabled={page === 1}>
          ‚¨ÖÔ∏è –ù–∞–∑–∞–¥
        </button>
        <span className="p-2">{page}</span>
        <button className="p-2 border" onClick={() => setPage(page + 1)}>
          –í–ø–µ—Ä–µ–¥ ‚û°Ô∏è
        </button>
      </div>
    </div>
  );
};

export default BooksList;
