import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; 
import axios from "axios";

const BookPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate(); 
  const [book, setBook] = useState<any>(null);
  const [isAvailable, setIsAvailable] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false); 
  const [returnDate, setReturnDate] = useState("");
  const [isBookRentedByUser, setIsBookRentedByUser] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [authorName, setAuthorName] = useState<string>("");

  useEffect(() => {
    fetchBook();
    checkAvailability();
    checkIfBookRentedByUser();
    checkAdmin();
  }, [id]);

  // 📌 Проверка роли администратора
  const checkAdmin = () => {
    const token = localStorage.getItem("token");
    if (!token) return;
  
    try {
      const base64Url = token.split(".")[1];
      const decodedPayload = JSON.parse(atob(base64Url.replace(/-/g, "+").replace(/_/g, "/")));
      const role = decodedPayload["http://schemas.microsoft.com/ws/2008/06/identity/claims/role"];
      setIsAdmin(role === "Admin");
    } catch (error) {
      console.error("Ошибка при проверке роли:", error);
    }
  };

  // 📌 Загрузка информации о книге
  const fetchBook = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}`);
      const bookData = response.data;
      setBook(bookData);

      if (!bookData.authorName && bookData.authorId) {
        fetchAuthorName(bookData.authorId);
      } else {
        setAuthorName(bookData.authorName);
      }
    } catch (error) {
      console.error("Ошибка при загрузке книги:", error);
    }
  };

  // 📌 Запрос имени автора
  const fetchAuthorName = async (authorId: number) => {
    try {
      const response = await axios.get(`http://localhost:7143/api/authors/${authorId}`);
      const author = response.data;
      setAuthorName(`${author.firstName} ${author.lastName}`);
    } catch (error) {
      console.error("Ошибка при загрузке автора:", error);
    }
  };

  // 📌 Проверка доступности книги
  const checkAvailability = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}/rentals`);
      setIsAvailable(response.data.length === 0);
    } catch (error) {
      console.error("Ошибка при проверке наличия книги:", error);
      setIsAvailable(false);
    }
  };

  // 📌 Проверка, арендовал ли пользователь книгу
  const checkIfBookRentedByUser = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get(`http://localhost:7143/api/books/${id}/is-rented`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setIsBookRentedByUser(response.data.isRented);
    } catch (error) {
      console.error("Ошибка при проверке аренды книги:", error);
    }
  };

  // 📌 Взять книгу в аренду
  const handleBorrowBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.post(
        "http://localhost:7143/api/books/borrow",
        { bookId: id, returnAt: returnDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setShowModal(false);
      setIsAvailable(false);
      setIsBookRentedByUser(true);
    } catch (error) {
      console.error("Ошибка при взятии книги:", error);
    }
  };

  // 📌 Вернуть книгу
  const handleReturnBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.post(`http://localhost:7143/api/books/return/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setIsAvailable(true);
      setIsBookRentedByUser(false);
    } catch (error) {
      console.error("Ошибка при возврате книги:", error);
    }
  };

  // 📌 Удалить книгу
  const handleDeleteBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.delete(`http://localhost:7143/api/books/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      navigate("/books");
    } catch (error) {
      console.error("Ошибка при удалении книги:", error);
    }
  };

  if (!book) return <p>Загрузка...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{book.title}</h1>

      {/* 📌 Вывод изображения книги */}
      {book.imagePath ? (
        <img
          src={`http://localhost:7143${book.imagePath}`} 
          alt={book.title}
          className="w-64 h-80 object-cover rounded-lg shadow-lg my-4"
        />
      ) : (
        <p className="text-gray-500">Нет изображения</p>
      )}

      <p><strong>ISBN:</strong> {book.isbn}</p>
      <p><strong>Жанр:</strong> {book.genre}</p>
      <p><strong>Описание:</strong> {book.description}</p>
      <p><strong>Автор:</strong> {authorName}</p> {/* ✅ Исправленный вывод имени автора */}
      <p>
        <strong>Статус:</strong>{" "}
        <span className={isAvailable ? "text-green-500" : "text-red-500"}>
          {isAvailable ? "В наличии" : "Нет в наличии"}
        </span>
      </p>

      {isAdmin && (
        <div className="mt-4 flex gap-4">
          <button
            className="bg-yellow-500 text-white px-4 py-2"
            onClick={() => navigate(`/edit-book/${book.id}`)}
          >
            ✏️ Редактировать
          </button>
          <button
            className="bg-red-500 text-white px-4 py-2"
            onClick={() => setShowDeleteModal(true)}
          >
            🗑 Удалить
          </button>
        </div>
      )}

      {showDeleteModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Вы уверены, что хотите удалить книгу?</h2>
            <div className="flex justify-between">
              <button className="bg-red-500 text-white px-4 py-2" onClick={handleDeleteBook}>
                ✅ Удалить
              </button>
              <button className="bg-gray-500 text-white px-4 py-2" onClick={() => setShowDeleteModal(false)}>
                ❌ Отмена
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookPage;
