import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; 
import axios from "axios";

const BookPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate(); 
  const [book, setBook] = useState<any>(null);
  const [isAvailable, setIsAvailable] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false); 
  const [returnDate, setReturnDate] = useState("");
  const [isBookRentedByUser, setIsBookRentedByUser] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [authorName, setAuthorName] = useState<string>("");

  useEffect(() => {
    fetchBook();
    checkAvailability();
    checkIfBookRentedByUser();
    checkAdmin();
  }, [id]);

  // üìå –ü—Ä–æ–≤–µ—Ä–∫–∞ —Ä–æ–ª–∏ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
  const checkAdmin = () => {
    const token = localStorage.getItem("token");
    if (!token) return;
  
    try {
      const base64Url = token.split(".")[1];
      const decodedPayload = JSON.parse(atob(base64Url.replace(/-/g, "+").replace(/_/g, "/")));
      const role = decodedPayload["http://schemas.microsoft.com/ws/2008/06/identity/claims/role"];
      setIsAdmin(role === "Admin");
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ —Ä–æ–ª–∏:", error);
    }
  };

  // üìå –ó–∞–≥—Ä—É–∑–∫–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –∫–Ω–∏–≥–µ
  const fetchBook = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}`);
      const bookData = response.data;
      setBook(bookData);

      if (!bookData.authorName && bookData.authorId) {
        fetchAuthorName(bookData.authorId);
      } else {
        setAuthorName(bookData.authorName);
      }
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–Ω–∏–≥–∏:", error);
    }
  };

  // üìå –ó–∞–ø—Ä–æ—Å –∏–º–µ–Ω–∏ –∞–≤—Ç–æ—Ä–∞
  const fetchAuthorName = async (authorId: number) => {
    try {
      const response = await axios.get(`http://localhost:7143/api/authors/${authorId}`);
      const author = response.data;
      setAuthorName(`${author.firstName} ${author.lastName}`);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∞–≤—Ç–æ—Ä–∞:", error);
    }
  };

  // üìå –ü—Ä–æ–≤–µ—Ä–∫–∞ –¥–æ—Å—Ç—É–ø–Ω–æ—Å—Ç–∏ –∫–Ω–∏–≥–∏
  const checkAvailability = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}/rentals`);
      setIsAvailable(response.data.length === 0);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –Ω–∞–ª–∏—á–∏—è –∫–Ω–∏–≥–∏:", error);
      setIsAvailable(false);
    }
  };

  // üìå –ü—Ä–æ–≤–µ—Ä–∫–∞, –∞—Ä–µ–Ω–¥–æ–≤–∞–ª –ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∫–Ω–∏–≥—É
  const checkIfBookRentedByUser = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get(`http://localhost:7143/api/books/${id}/is-rented`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setIsBookRentedByUser(response.data.isRented);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –∞—Ä–µ–Ω–¥—ã –∫–Ω–∏–≥–∏:", error);
    }
  };

  // üìå –í–∑—è—Ç—å –∫–Ω–∏–≥—É –≤ –∞—Ä–µ–Ω–¥—É
  const handleBorrowBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.post(
        "http://localhost:7143/api/books/borrow",
        { bookId: id, returnAt: returnDate },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setShowModal(false);
      setIsAvailable(false);
      setIsBookRentedByUser(true);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤–∑—è—Ç–∏–∏ –∫–Ω–∏–≥–∏:", error);
    }
  };

  // üìå –í–µ—Ä–Ω—É—Ç—å –∫–Ω–∏–≥—É
  const handleReturnBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.post(`http://localhost:7143/api/books/return/${id}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setIsAvailable(true);
      setIsBookRentedByUser(false);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤–æ–∑–≤—Ä–∞—Ç–µ –∫–Ω–∏–≥–∏:", error);
    }
  };

  // üìå –£–¥–∞–ª–∏—Ç—å –∫–Ω–∏–≥—É
  const handleDeleteBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      await axios.delete(`http://localhost:7143/api/books/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      navigate("/books");
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ —É–¥–∞–ª–µ–Ω–∏–∏ –∫–Ω–∏–≥–∏:", error);
    }
  };

  if (!book) return <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{book.title}</h1>

      {/* üìå –í—ã–≤–æ–¥ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∫–Ω–∏–≥–∏ */}
      {book.imagePath ? (
        <img
          src={`http://localhost:7143${book.imagePath}`} 
          alt={book.title}
          className="w-64 h-80 object-cover rounded-lg shadow-lg my-4"
        />
      ) : (
        <p className="text-gray-500">–ù–µ—Ç –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è</p>
      )}

      <p><strong>ISBN:</strong> {book.isbn}</p>
      <p><strong>–ñ–∞–Ω—Ä:</strong> {book.genre}</p>
      <p><strong>–û–ø–∏—Å–∞–Ω–∏–µ:</strong> {book.description}</p>
      <p><strong>–ê–≤—Ç–æ—Ä:</strong> {authorName}</p> {/* ‚úÖ –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–Ω—ã–π –≤—ã–≤–æ–¥ –∏–º–µ–Ω–∏ –∞–≤—Ç–æ—Ä–∞ */}
      <p>
        <strong>–°—Ç–∞—Ç—É—Å:</strong>{" "}
        <span className={isAvailable ? "text-green-500" : "text-red-500"}>
          {isAvailable ? "–í –Ω–∞–ª–∏—á–∏–∏" : "–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏"}
        </span>
      </p>

      {isAdmin && (
        <div className="mt-4 flex gap-4">
          <button
            className="bg-yellow-500 text-white px-4 py-2"
            onClick={() => navigate(`/edit-book/${book.id}`)}
          >
            ‚úèÔ∏è –†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å
          </button>
          <button
            className="bg-red-500 text-white px-4 py-2"
            onClick={() => setShowDeleteModal(true)}
          >
            üóë –£–¥–∞–ª–∏—Ç—å
          </button>
        </div>
      )}

