import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const BookPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate(); 
  const [book, setBook] = useState<any>(null);
  const [isAvailable, setIsAvailable] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [returnDate, setReturnDate] = useState("");
  const [isBookRentedByUser, setIsBookRentedByUser] = useState(false);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    fetchBook();
    checkAvailability();
    checkIfBookRentedByUser();
    checkAdmin();
  }, [id]);

  const checkAdmin = () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const base64Url = token.split(".")[1];
      const decodedPayload = JSON.parse(atob(base64Url.replace(/-/g, "+").replace(/_/g, "/")));

      setIsAdmin(decodedPayload.role === "Admin");
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ —Ä–æ–ª–∏:", error);
    }
  };
  const fetchBook = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}`);
      setBook(response.data);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–Ω–∏–≥–∏:", error);
    }
  };

  const checkAvailability = async () => {
    try {
      const response = await axios.get(`http://localhost:7143/api/books/${id}/rentals`);
      console.log("–û—Ç–≤–µ—Ç –æ—Ç bookrentals:", response.data);

      const isRented = response.data.length > 0;
      setIsAvailable(!isRented);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ –Ω–∞–ª–∏—á–∏—è –∫–Ω–∏–≥–∏:", error);
      setIsAvailable(false);
    }
  };

  const checkIfBookRentedByUser = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;
  
      const base64Url = token.split(".")[1];
      const decodedPayload = JSON.parse(atob(base64Url.replace(/-/g, "+").replace(/_/g, "/")));
  
      console.log("üìå –î–µ–∫–æ–¥–∏—Ä–æ–≤–∞–Ω–Ω—ã–π —Ç–æ–∫–µ–Ω:", decodedPayload);
  
      const response = await axios.get(`http://localhost:7143/api/books/${id}/is-rented`, {
        headers: { Authorization: `Bearer ${token}` },
      });
  
      console.log("üìå –û—Ç–≤–µ—Ç API (–∫–Ω–∏–≥–∞ –∞—Ä–µ–Ω–¥–æ–≤–∞–Ω–∞?):", response.data);
  
      setIsBookRentedByUser(response.data.isRented);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø—Ä–æ–≤–µ—Ä–∫–µ, –≤–∑—è–ª –ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –∫–Ω–∏–≥—É:", error);
    }
  };
  
  

  const handleBorrowBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("–¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç.");
        return;
      }

      const response = await axios.post(
        "http://localhost:7143/api/books/borrow",
        {
          bookId: id,
          returnAt: returnDate,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      console.log("–ö–Ω–∏–≥–∞ —É—Å–ø–µ—à–Ω–æ –≤–∑—è—Ç–∞:", response.data);
      setShowModal(false);
      setIsAvailable(false);
      setIsBookRentedByUser(true);
    } catch (error) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤–∑—è—Ç–∏–∏ –∫–Ω–∏–≥–∏:", error);
    }
  };

  const handleReturnBook = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå –¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç.");
        return;
      }
  
      console.log("üìå –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ –≤–æ–∑–≤—Ä–∞—Ç –∫–Ω–∏–≥–∏:", id);
  
      const response = await axios.post(
        `http://localhost:7143/api/books/return/${id}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
  
      console.log("‚úÖ –ö–Ω–∏–≥–∞ —É—Å–ø–µ—à–Ω–æ –≤–æ–∑–≤—Ä–∞—â–µ–Ω–∞:", response.data);
      setIsAvailable(true);
      setIsBookRentedByUser(false);
    } catch (error: any) {
      console.error("üõë –û—à–∏–±–∫–∞ –ø—Ä–∏ –≤–æ–∑–≤—Ä–∞—Ç–µ –∫–Ω–∏–≥–∏:", error.response?.data || error.message);
    }
  };

  if (!book) return <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">{book.title}</h1>
      <p><strong>ISBN:</strong> {book.isbn}</p>
      <p><strong>–ñ–∞–Ω—Ä:</strong> {book.genre}</p>
      <p><strong>–û–ø–∏—Å–∞–Ω–∏–µ:</strong> {book.description}</p>
      <p><strong>–ê–≤—Ç–æ—Ä:</strong> {book.authorName}</p>
      <p>
        <strong>–°—Ç–∞—Ç—É—Å:</strong>{" "}
        <span className={isAvailable ? "text-green-500" : "text-red-500"}>
          {isAvailable ? "–í –Ω–∞–ª–∏—á–∏–∏" : "–ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏"}
        </span>
      </p>

      {isAvailable ? (
        <button className="bg-blue-500 text-white px-4 py-2 mt-4" onClick={() => setShowModal(true)}>
            üìñ –í–∑—è—Ç—å –∫–Ω–∏–≥—É
        </button>
        ) : isBookRentedByUser ? (
        <button className="bg-red-500 text-white px-4 py-2 mt-4" onClick={handleReturnBook}>
            üìö –í–µ—Ä–Ω—É—Ç—å –∫–Ω–∏–≥—É
        </button>
        ) : (
        <p className="text-red-500 font-bold">‚ùå –ù–µ—Ç –≤ –Ω–∞–ª–∏—á–∏–∏</p>
        )}


      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">–í—ã–±–µ—Ä–∏—Ç–µ –¥–∞—Ç—É –≤–æ–∑–≤—Ä–∞—Ç–∞</h2>
            <input
              className="border p-2 mb-2 w-full"
              type="date"
              min={new Date().toISOString().split("T")[0]}
              max={new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split("T")[0]}
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
            />
            <div className="flex justify-between">
              <button className="bg-green-500 text-white px-4 py-2" onClick={handleBorrowBook}>
                ‚úÖ –í–∑—è—Ç—å –∫–Ω–∏–≥—É
              </button>
              <button className="bg-gray-500 text-white px-4 py-2" onClick={() => setShowModal(false)}>
                ‚ùå –û—Ç–º–µ–Ω–∞
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookPage;
