import React, { useEffect, useState } from "react";
import { getBooks } from "../api";
import { useNavigate } from "react-router-dom";

const BooksList: React.FC = () => {
  const [books, setBooks] = useState([]);
  const [search, setSearch] = useState("");
  const [genre, setGenre] = useState("");
  const [author, setAuthor] = useState("");
  const [page, setPage] = useState(1);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchBooks();
    checkAdmin();
  }, [search, genre, author, page]);

  const fetchBooks = async () => {
    try {
      const data = await getBooks(search, genre, author, page);
      setBooks(data);
    } catch (error) {
      console.error("Ошибка при загрузке книг:", error);
    }
  };

  const checkAdmin = () => {
    const token = localStorage.getItem("token");
  
    if (!token) {
      setIsAdmin(false);
      return;
    }
  
    try {
      const base64Url = token.split(".")[1];
      if (!base64Url) throw new Error("Invalid token format");
  
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const decodedPayload = JSON.parse(atob(base64));
  
      setIsAdmin(decodedPayload.role === "Admin");
    } catch (error) {
      console.error("Ошибка декодирования токена:", error);
      setIsAdmin(false); // Если ошибка - считаем, что пользователь не админ
    }
  };
  

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Список книг</h1>

      {/* Кнопка добавления книги (только для администратора) */}
      {isAdmin && (
        <button
          className="bg-green-500 text-white px-4 py-2 mb-4"
          onClick={() => navigate("/add-book")}
        >
          ➕ Добавить книгу
        </button>
      )}

      {/* Поля фильтрации */}
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Поиск по названию..."
          className="border p-2"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="text"
          placeholder="Введите жанр..."
          className="border p-2"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
        <input
          type="text"
          placeholder="Введите имя автора..."
          className="border p-2"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
      </div>

      {/* Список книг */}
      <div className="grid grid-cols-3 gap-4">
        {books.length === 0 ? (
          <p>Книги не найдены</p>
        ) : (
          books.map((book: any) => (
            <div key={book.id} className="border p-4 rounded shadow">
              <h2 className="text-xl font-bold">{book.title}</h2>
              <p>{book.genre}</p>
              <p>Автор: {book.authorName}</p> {/* Отображаем имя автора */}
            </div>
          ))
        )}
      </div>

      {/* Пагинация */}
      <div className="flex justify-center mt-4">
        <button className="p-2 border" onClick={() => setPage(page - 1)} disabled={page === 1}>
          ⬅️ Назад
        </button>
        <span className="p-2">{page}</span>
        <button className="p-2 border" onClick={() => setPage(page + 1)}>
          Вперед ➡️
        </button>
      </div>
    </div>
  );
};

export default BooksList;
