import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddBook: React.FC = () => {
  const [title, setTitle] = useState("");
  const [genre, setGenre] = useState("");
  const [authorName, setAuthorName] = useState("");
  const [authors, setAuthors] = useState<{ id: string; name: string }[]>([]);
  const [selectedAuthor, setSelectedAuthor] = useState<string | null>(null);
  const [showAddAuthor, setShowAddAuthor] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get("http://localhost:7143/api/authors");
      setAuthors(response.data);
    } catch (error) {
      console.error("Ошибка при загрузке авторов:", error);
    }
  };

  const handleAddBook = async () => {
    try {
      let authorId = selectedAuthor;

      if (!selectedAuthor) {
        const authorResponse = await axios.post("http://localhost:7143/api/authors", { name: authorName });
        authorId = authorResponse.data.id;
      }

      await axios.post("http://localhost:7143/api/books", {
        title,
        genre,
        authorId,
      });

      navigate("/books");
    } catch (error) {
      console.error("Ошибка при добавлении книги:", error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Добавление книги</h1>

      <input
        className="border p-2 mb-2 w-full"
        type="text"
        placeholder="Название книги"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        className="border p-2 mb-2 w-full"
        type="text"
        placeholder="Жанр"
        value={genre}
        onChange={(e) => setGenre(e.target.value)}
      />

      <select
        className="border p-2 mb-2 w-full"
        value={selectedAuthor || ""}
        onChange={(e) => setSelectedAuthor(e.target.value)}
      >
        <option value="">Выберите автора</option>
        {authors.map((author) => (
          <option key={author.id} value={author.id}>
            {author.name}
          </option>
        ))}
      </select>

      <button
        className="bg-gray-500 text-white px-4 py-2 mb-2"
        onClick={() => setShowAddAuthor(true)}
      >
        Добавить нового автора
      </button>

      {showAddAuthor && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h2 className="text-xl font-bold mb-4">Добавление автора</h2>
            <input
              className="border p-2 mb-2 w-full"
              type="text"
              placeholder="Имя автора"
              value={authorName}
              onChange={(e) => setAuthorName(e.target.value)}
            />
            <div className="flex justify-between">
              <button
                className="bg-blue-500 text-white px-4 py-2"
                onClick={() => {
                  setShowAddAuthor(false);
                }}
              >
                Закрыть
              </button>
            </div>
          </div>
        </div>
      )}

      <button
        className="bg-green-500 text-white px-4 py-2 mt-4"
        onClick={handleAddBook}
      >
        Добавить книгу
      </button>
    </div>
  );
};

export default AddBook;
